package com.ds.ms.article.controller;import com.ds.ms.article.exceptions.VehicleNotFoundException;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseStatus;import org.springframework.web.bind.annotation.RestControllerAdvice;import org.springframework.web.servlet.mvc.method.annotation.ResponseEntityExceptionHandler;@RestControllerAdvicepublic class Advice extends ResponseEntityExceptionHandler {    @ExceptionHandler(VehicleNotFoundException.class)    @ResponseStatus(HttpStatus.NOT_FOUND)    protected String handleConflict(VehicleNotFoundException ex) {        return ex.getMessage();    }}